{"changed":true,"filter":false,"title":"index.php","tooltip":"/index.php","value":"<!DOCTYPE html>\n<html>\n<head>\n    <title>HTML5/JavaScript Event Calendar</title>\n\t<!-- demo stylesheet -->\n    \t<link type=\"text/css\" rel=\"stylesheet\" href=\"media/layout.css\" />\n\n        <link type=\"text/css\" rel=\"stylesheet\" href=\"themes/calendar_g.css\" />\n        <link type=\"text/css\" rel=\"stylesheet\" href=\"themes/calendar_green.css\" />\n        <link type=\"text/css\" rel=\"stylesheet\" href=\"themes/calendar_traditional.css\" />\n        <link type=\"text/css\" rel=\"stylesheet\" href=\"themes/calendar_transparent.css\" />\n        <link type=\"text/css\" rel=\"stylesheet\" href=\"themes/calendar_white.css\" />\n\n\t<!-- helper libraries -->\n\t<script src=\"js/jquery-1.9.1.min.js\" type=\"text/javascript\"></script>\n\n\t<!-- daypilot libraries -->\n        <script src=\"js/daypilot/daypilot-all.min.js\" type=\"text/javascript\"></script>\n\n</head>\n<body>\n        <div id=\"header\">\n\t\t\t<div class=\"bg-help\">\n\t\t\t\t<div class=\"inBox\">\n\t\t\t\t\t<h1 id=\"logo\"><a href='https://code.daypilot.org/17910/html5-event-calendar-open-source'>HTML5/JavaScript Event Calendar</a></h1>\n\t\t\t\t\t<p id=\"claim\"><a href=\"https://javascript.daypilot.org/\">DayPilot for JavaScript</a> - AJAX Calendar/Scheduling Widgets for JavaScript/HTML5/jQuery</p>\n\t\t\t\t\t<hr class=\"hidden\" />\n\t\t\t\t</div>\n\t\t\t</div>\n        </div>\n        <div class=\"shadow\"></div>\n        <div class=\"hideSkipLink\">\n        </div>\n        <div class=\"main\">\n\n            <div style=\"float:left; width: 160px;\">\n                <div id=\"nav\"></div>\n            </div>\n            <div style=\"margin-left: 160px;\">\n\n                <div class=\"space\">\n                    Theme: <select id=\"theme\">\n                        <option value=\"calendar_default\">Default</option>\n                        <option value=\"calendar_white\">White</option>\n                        <option value=\"calendar_g\">Google-Like</option>\n                        <option value=\"calendar_green\">Green</option>\n                        <option value=\"calendar_traditional\">Traditional</option>\n                        <option value=\"calendar_transparent\">Transparent</option>\n                    </select>\n                </div>\n\n                <div id=\"dp\"></div>\n            </div>\n\n            <script type=\"text/javascript\">\n\n                var nav = new DayPilot.Navigator(\"nav\");\n                nav.showMonths = 3;\n                nav.skipMonths = 3;\n                nav.selectMode = \"week\";\n                nav.onTimeRangeSelected = function(args) {\n                    dp.startDate = args.day;\n                    dp.update();\n                    loadEvents();\n                };\n                nav.init();\n\n                var dp = new DayPilot.Calendar(\"dp\");\n                dp.viewType = \"Week\";\n\n                dp.eventDeleteHandling = \"Update\";\n\n                dp.onEventDeleted = function(args) {\n                    $.post(\"backend_delete.php\",\n                        {\n                            id: args.e.id()\n                        },\n                        function() {\n                            console.log(\"Deleted.\");\n                        });\n                };\n\n                dp.onEventMoved = function(args) {\n                    $.post(\"backend_move.php\",\n                            {\n                                id: args.e.id(),\n                                newStart: args.newStart.toString(),\n                                newEnd: args.newEnd.toString()\n                            },\n                            function() {\n                                console.log(\"Moved.\");\n                            });\n                };\n\n                dp.onEventResized = function(args) {\n                    $.post(\"backend_resize.php\",\n                            {\n                                id: args.e.id(),\n                                newStart: args.newStart.toString(),\n                                newEnd: args.newEnd.toString()\n                            },\n                            function() {\n                                console.log(\"Resized.\");\n                            });\n                };\n\n                // event creating\n                dp.onTimeRangeSelected = function(args) {\n                    var name = prompt(\"New event name:\", \"Event\");\n                    dp.clearSelection();\n                    if (!name) return;\n                    var e = new DayPilot.Event({\n                        start: args.start,\n                        end: args.end,\n                        id: DayPilot.guid(),\n                        resource: args.resource,\n                        text: name\n                    });\n                    dp.events.add(e);\n\n                    $.post(\"backend_create.php\",\n                            {\n                                start: args.start.toString(),\n                                end: args.end.toString(),\n                                name: name\n                            },\n                            function() {\n                                console.log(\"Created.\");\n                            });\n\n                };\n\n                dp.onEventClick = function(args) {\n                    alert(\"clicked: \" + args.e.id());\n                };\n\n                dp.init();\n\n                loadEvents();\n\n                function loadEvents() {\n                    dp.events.load(\"backend_events.php\");\n                }\n\n            </script>\n\n            <script type=\"text/javascript\">\n            $(document).ready(function() {\n                $(\"#theme\").change(function(e) {\n                    dp.theme = this.value;\n                    dp.update();\n                });\n            });\n            </script>\n\n        </div>\n        <div class=\"clear\">\n        </div>\n\n</body>\n</html>\n\n","undoManager":{"mark":-2,"position":0,"stack":[[{"start":{"row":0,"column":0},"end":{"row":0,"column":1},"action":"remove","lines":["ï»¿"],"id":2}]]},"ace":{"folds":[],"scrolltop":210,"scrollleft":0,"selection":{"start":{"row":12,"column":0},"end":{"row":12,"column":0},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":{"row":13,"state":"start","mode":"ace/mode/php"}},"timestamp":1576593161134}